insert into `tcnote` (`note_name`, `author_name`, `from_url`, `blob_content`, `create_date`, `flag`, `noteBook`, `noteBookGroup`) values('Quick Tip: Add Facebook Login to Your Android App.html','高广金','http://www.tuicool.com/articles/fiAJ7zI','<!DOCTYPE HTML>\n\n<!--\n ______________ \n< TUICOOL.COM >\n -------------- \n        \\   ^__^\n         \\  (**)\\__$__$__\n            (__)\\       )\\/\\\n             U  ||------|\n                ||     ||\n-->\n<head>\n  <meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n  <meta content=\"authenticity_token\" name=\"csrf-param\" />\n<meta content=\"s+F2mzIYQdxVnMknECpsf8zLPP0krRHvhklNgUIwDD8=\" name=\"csrf-token\" />\n    <title>\n            Quick Tip: Add Facebook Login to Your Android App - 推酷\n   </title>\n    <meta name=\"description\" content=\"Quick Tip: Add Facebook Login to Your Android App\"/>\n    <meta property=\"qc:admins\" content=\"2040660764645137746375\" />\n  <link rel=\"shortcut icon\" href=\"http://static0.tuicool.com/favicon.ico\" type=\"image/x-icon\" />\n  <link href=\"http://asset.tuicool.com/assets/application-77e2835bfa2a5c61a6c79d3fcb00cd7b.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" />\n  <link href=\"http://netdna.bootstrapcdn.com/font-awesome/4.0.3/css/font-awesome.min.css\" rel=\"stylesheet\">\n  <script src=\"http://asset.tuicool.com/assets/application-5272668a8ed427c5225cac7c6110da64.js\" type=\"text/javascript\"></script>\n\n  <!--[if IE 7]>\n  <link rel=\"stylesheet\" href=\"http://assets.tuicool.com/assets/font-awesome-ie7.min.css\">\n  <![endif]--> \n    <script type=\"text/javascript\" src=\"http://static2.tuicool.com/assets/tip.js?t=3\"></script>\n  \n  <script type=\"text/javascript\" src=\"http://cbjs.baidu.com/js/m.js\"></script>\n<script type=\"text/javascript\" src=\"http://static1.tuicool.com/assets/spin.min.js\"></script>\n<link rel=\"stylesheet\" href=\"http://static0.tuicool.com/assets/github.css\">\n\n</head>\n<body>\n  \n  <div id=\"header\" class=\"navbar-fixed-top\">\n    <div class=\"container\">\n      <div class=\"navbar\">\n        <div class=\"navbar-inner\">\n          <a class=\"btn btn-navbar\" data-toggle=\"collapse\" data-target=\".nav-collapse\"> \n            <span class=\"icon-bar\"></span> \n            <span class=\"icon-bar\"></span>\n            <span class=\"icon-bar\"></span>\n            <span class=\"icon-bar\"></span> \n          </a>\n          <a href=\"http://www.tuicool.com/\" class=\"brand\">推酷</a>        \n        <nav class=\"nav-collapse collapse\">\n            <ul class=\"nav navbar primary-nav\">                            \n              <li class=\"active\">\n                <a href=\"http://www.tuicool.com/ah\">\n                  文章\n                </a>\n              </li>              \n              <li class=\"\">\n                <a href=\"http://www.tuicool.com/sites/hot\">\n                  站点\n                </a>\n              </li>\n              <li class=\"\">\n                <a href=\"http://www.tuicool.com/topics\">\n                  主题\n                </a>\n              </li>\n              <li class=\"\">\n                <a href=\"http://course.tuicool.com/\">\n                  公开课\n                </a>\n              </li>\n              <li class=\"\">\n                <a href=\"http://huodong.tuicool.com/\">\n                  活动\n                </a>\n              </li>\n              <li class=\"\">\n                <a href=\"http://www.tuicool.com/mobile\">\n                  客户端\n                    <sup style=\"font-size:0.8em;color: #16A085;\">荐</sup>\n                </a>\n              </li>\n              <li class=\"dropdown  \">\n                <a href=\"#\" class=\"dropdown-toggle\" data-toggle=\"dropdown\">周刊 <b class=\"caret\"></b></a>\n                <ul class=\"dropdown-menu\">\n                  <li><a href=\"http://www.tuicool.com/mags\">编程狂人</a></li>\n                  <li><a href=\"http://www.tuicool.com/mags/design\">设计匠艺</a></li> \n                  <li><a href=\"http://www.tuicool.com/articles/weekly\">一周拾遗</a></li>                  \n                </ul>\n              </li>\n              \n               <li class=\"dropdown  \">\n                <a href=\"#\" class=\"dropdown-toggle\" data-toggle=\"dropdown\">更多 <b class=\"caret\"></b></a>\n                <ul class=\"dropdown-menu\">\n                  <li><a href=\"http://www.tuicool.com/bbs\">讨论区</a></li>  \n                  <li><a href=\"http://www.tuicool.com/about\">关于我们</a></li>                 \n                </ul>\n              </li>\n              \n              </ul>\n            <form class=\"navbar-search pull-left\" action=\"http://www.tuicool.com/search\">\n              <input type=\"text\" class=\"search-query span2\" name=\"kw\" placeholder=\"搜索\">\n            </form>\n            <ul class=\"nav pull-right\">\n                <li><a href=\"http://www.tuicool.com/login\">登录</a></li>\n            </ul>\n          </nav>\n        </div>\n      </div>\n  </div>   \n</div>\n  <div id =\"flash_container\" class=\"noPrint\">    \n  </div>\n  \n  <div class=\"container-fluid\">  \n    <div class=\"frd_pos\">\n    <div class=\"row-fluid\" style=\"margin-top: 15px\">\n        <div>\n            <script type=\"text/javascript\">BAIDU_CLB_fillSlot(\"1076478\");</script>\n        </div>\n    </div>\n</div>\n<div class=\"row-fluid article_row_fluid\">\n    <div class=\"span8 contant article_detail_bg\">\n        <h1>Quick Tip: Add Facebook Login to Your Android App</h1>\n        <div class=\"article_meta\">\n            <span class=\"timestamp\">时间&nbsp;2015-05-19 00:25:18\n            </span>\n            <span class=\"from\">\n                <i class=\"icon-globe\"></i>\n                    <a class=\"cut cut28 from\" href=\"/sites/eQv6Fv\" target=\"_blank\">Mobiletuts+\n                    </a>\n            </span>\n            <span class=\"source\" style=\"display:block;\"><i style=\"float:left;\">原文</i>&nbsp; \n                <a class=\"cut cut70\" href=\"http://www.tuicool.com/articles/goto?id=fiAJ7zI\" style=\"display:inline-block;\">http://code.tutsplus.com/tutorials/quick-tip-add-facebook-login-to-your-android-app--cms-23837</a>\n            </span>\n        </div>\n        <div class=\"article_body\" id=\"nei\">\n            <div> \n <p>Facebook Login provides a convenient and secure way for people to log in to an app without having to go through a sign-up process first. Using the latest version of Facebook\'s SDK for Android, it takes only a few minutes to add this feature to your app. </p> \n <p>In this quick tip, you will learn how to add a Facebook login button to an Android app and handle the events to log a user in using Facebook Login. </p> \n <h2>Prerequisites </h2> \n <p>Before you begin, make sure you have access to the following: </p> \n <ul> \n  <li> the latest version of <a href=\"https://developer.android.com/sdk/index.html\" target=\"_self\" rel=\"nofollow,noindex\">Android Studio</a> </li> \n  <li> a&nbsp; <a href=\"https://www.facebook.com/\" target=\"_self\" rel=\"nofollow,noindex\">Facebook</a> account </li> \n </ul> \n <h2> <span>1.</span> Register Your App </h2> \n <p> All apps that use the Facebook SDK must be registered with Facebook. Log in to the <a href=\"https://developers.facebook.com/apps/\" target=\"_self\" rel=\"nofollow,noindex\">Facebook Developers website</a> and click <strong>Create a New App</strong> in the top right. </p> \n <figure> \n  <img src=\"http://img0.tuicool.com/7jMvaeB.png\" class=\"alignCenter\" /> \n </figure> \n <p> You are presented with a form that asks for the app\'s <strong>Display Name</strong> , <strong>Namespace</strong> , and <strong>Category</strong> . Enter the required fields and&nbsp;click&nbsp; <strong>Create App ID</strong> . </p> \n <figure> \n  <img src=\"http://img2.tuicool.com/Vfa2yu.png\" class=\"alignCenter\" /> \n </figure> \n <p> In the next screen, you are able to see your <strong>Application ID</strong> . Make a note of it, because you will be needing it later in this tutorial. </p> \n <figure> \n  <img src=\"http://img0.tuicool.com/7FnUziA.png\" class=\"alignCenter\" /> \n </figure> \n <p> Open <strong>Settings</strong> from the left and click the&nbsp; <strong>Add Platform</strong> button. From the pop-up, select <strong>Android</strong> . </p> \n <figure> \n  <img src=\"http://img1.tuicool.com/BBZrIz.png\" class=\"alignCenter\" /> \n </figure> \n <p> In the next&nbsp;form, enter the package name of your app and the name of your <code>Activity</code> . If you haven\'t created your app or <code>Activity</code> yet, make sure you remember the values you entered. </p> \n <p> To fill in the <strong>Key Hashes</strong> field, open a terminal window&nbsp;and run the <code>keytool</code> command to generate a key hash using the debug keystore located at <strong>~/.android/debug.keystore</strong> . This is what the command should look like. </p> \n <pre class=\"prettyprint\">keytool -exportcert -alias androiddebugkey -keystore ~/.android/debug.keystore | openssl sha1 -binary | openssl base64</pre> \n <p> The default password for the debug keystore is <strong>android</strong> . Enter that password&nbsp;when prompted. The output of the command should be a string of&nbsp;28 characters. Copy it, go back to your browser, and paste the string into the <strong>Key Hashes</strong> field as shown below. </p> \n <figure> \n  <img src=\"http://img0.tuicool.com/Zf6fqu.png\" class=\"alignCenter\" /> \n </figure> \n <p> Make sure <strong>Single Sign On</strong> is set to&nbsp; <strong>Yes</strong> and&nbsp;click the <strong>Save Changes</strong> button. Your app is now registered. </p> \n <h2> <span>2.</span> Add&nbsp;Facebook SDK to Your Project </h2> \n <p> The Facebook SDK is available on <a href=\"http://search.maven.org/\" target=\"_self\" rel=\"nofollow,noindex\">Maven Central</a> . To use this repository, edit the <strong>build.gradle</strong> file in your project\'s <strong>app</strong> directory and add the following code to it before the list of <strong>dependencies</strong> : </p> \n <pre>repositories {\r\n    mavenCentral()\r\n}</pre> \n <p> You can now add the&nbsp;Facebook SDK to your project as a <strong>compile</strong> dependency. Add the following code to the list of <strong>dependencies</strong> : </p> \n <pre>compile \'com.facebook.android:facebook-android-sdk:4.0.0\'</pre> \n <h2> <span>3.</span> Create an Activity </h2> \n <h3>Step 1: Define the Layout </h3> \n <p> Create a new layout named <strong>main_activity.xml</strong> in <strong>res/layout</strong> . This is going to be&nbsp;a very simple layout with only two widgets: </p> \n <ul> \n  <li> a&nbsp; <code>LoginButton</code> to allow the user to log in to Facebook </li> \n  <li> a&nbsp; <code>TextView</code> to display the result of the latest login attempt </li> \n </ul> \n <p> You can place them inside a <code>RelativeLayout</code> . After including attributes for padding and positioning the widgets, the layout\'s&nbsp;XML will look something like this: </p> \n <pre class=\"prettyprint\">&lt;?xml version=&quot;1.0&quot; encoding=&quot;utf-8&quot;?&gt;\r\n&lt;RelativeLayout xmlns:android=&quot;http://schemas.android.com/apk/res/android&quot;\r\n	android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;match_parent&quot;\r\n	android:padding=&quot;16dp&quot;\r\n	&gt;\r\n	&lt;TextView\r\n		android:layout_width=&quot;wrap_content&quot;\r\n		android:layout_height=&quot;wrap_content&quot;\r\n		android:id=&quot;@+id/info&quot;\r\n		android:layout_alignParentLeft=&quot;true&quot;\r\n		android:layout_alignParentTop=&quot;true&quot;\r\n		android:textSize=&quot;18sp&quot;\r\n		/&gt;\r\n	&lt;com.facebook.login.widget.LoginButton\r\n		android:id=&quot;@+id/login_button&quot;\r\n		android:layout_width=&quot;wrap_content&quot;\r\n		android:layout_height=&quot;wrap_content&quot;\r\n		android:layout_centerInParent=&quot;true&quot;\r\n		/&gt;\r\n&lt;/RelativeLayout&gt;\n</pre> \n <h3>Step 2: Create the Class </h3> \n <p> Create a new Java class that extends <code>Activity</code> and name it <strong>MainActivity.java</strong> . Remember that the name of this class and the package that it belongs to should match the values you entered while registering your app with Facebook. </p> \n <p>Declare the widgets you defined in the activity\'s layout&nbsp;as fields of this class.</p> \n <pre>private TextView info;\r\nprivate LoginButton loginButton;</pre> \n <p> Declare a <code>CallbackManager</code> as another field. The <code>CallbackManager</code> , as its name suggests, is used to manage the callbacks used in the app. </p> \n <pre>private CallbackManager callbackManager;</pre> \n <p> The SDK needs to be initialized before using any of its methods. You can do so by calling <code>sdkInitialize</code> and passing the application\'s context to it. Add the following code to the <code>onCreate</code> method of your <code>Activity</code> : </p> \n <pre>FacebookSdk.sdkInitialize(getApplicationContext());</pre> \n <p> Next, initialize your instance of <code>CallbackManager</code> using the&nbsp; <code>CallbackManager.Factory.create</code> method. </p> \n <pre>callbackManager = CallbackManager.Factory.create();</pre> \n <p> Call <code>setContentView</code> to set the layout defined in the previous step as the layout of this&nbsp; <code>Activity</code> and then use <code>findViewById</code> to initialize the widgets. </p> \n <pre class=\"prettyprint\">setContentView(R.layout.main_activity);\r\ninfo = (TextView)findViewById(R.id.info);\r\nloginButton = (LoginButton)findViewById(R.id.login_button);</pre> \n <p> It\'s time to create a callback to handle the results of the login attempts and register it with the <code>CallbackManager</code> . Custom callbacks should implement <code>FacebookCallback</code> . The interface has methods to handle each possible outcome of a login attempt: </p> \n <ul> \n  <li> If the login&nbsp;attempt is successful,&nbsp; <code>onSuccess</code> is called. </li> \n  <li> If the user cancels the login attempt, <code>onCancel</code> is called. </li> \n  <li> If an error occurs, <code>onError</code> is called. </li> \n </ul> \n <p> To register the custom callback, use the <code>registerCallback</code> method. The code to create and register the callback should look like this: </p> \n <pre class=\"prettyprint\">loginButton.registerCallback(callbackManager, new FacebookCallback&lt;LoginResult&gt;() {\r\n	@Override\r\n	public void onSuccess(LoginResult loginResult) {\r\n	}\r\n	@Override\r\n	public void onCancel() {\r\n	}\r\n	@Override\r\n	public void onError(FacebookException e) {\r\n	}\r\n});\n</pre> \n <p> You can now add code to these methods to display appropriate messages using the <code>setText</code> method of the <code>TextView</code> . </p> \n <p> When the <code>onSuccess</code> method is called, a <code>LoginResult</code> is passed as a parameter. Retrieve the access token it contains using <code>getAccessToken</code> and use its <code>getUserId</code> method to get the user\'s ID. To get the token in the form of a <code>String</code> , use <code>getToken</code> . Display these values in the <code>TextView</code> by adding the following code to the <code>onSuccess</code> method: </p> \n <pre class=\"prettyprint\">info.setText(\r\n    &quot;User ID: &quot; \r\n    + loginResult.getAccessToken().getUserId() \r\n	+ &quot;\\n&quot; +\r\n    &quot;Auth Token: &quot;\r\n	+ loginResult.getAccessToken().getToken()\r\n);</pre> \n <p> If the user cancel\'s the login attempt, we display&nbsp;a message saying &quot;Login attempt canceled&quot;. Add the following code to the <code>onCancel</code> method: </p> \n <pre>info.setText(&quot;Login attempt canceled.&quot;);</pre> \n <p> Similarly, add the following code to the <code>onError</code> method: </p> \n <pre>info.setText(&quot;Login attempt failed.&quot;);</pre> \n <p> Tapping the login button starts off a new <code>Activity</code> , which returns a result. To receive and handle the result, override the <code>onActivityResult</code> method of your <code>Activity</code> and pass its parameters to the <code>onActivityResult</code> method of <code>CallbackManager</code> . </p> \n <pre class=\"prettyprint\">@Override\r\nprotected void onActivityResult(int requestCode, int resultCode, Intent data) {\r\n    callbackManager.onActivityResult(requestCode, resultCode, data);\r\n}</pre> \n <h2> <span>4.</span> Add the Facebook Application ID </h2> \n <p> The application ID you received when you registered your app should be added as a <strong>string</strong> in your project\'s <strong>res/values/strings.xml</strong> . For this tutorial, call the string <strong>facebook_app_id</strong> . </p> \n <pre class=\"prettyprint\">&lt;string name=&quot;facebook_app_id&quot;&gt;123456908761030&lt;/string&gt;</pre> \n <h2> <span>5.</span> Edit the Manifest </h2> \n <p> Define your <code>Activity</code> in the <strong>AndroidManifest.xml</strong> . If it is the first <code>Activity</code> of your app, you should also add an <code>intent-filter</code> that responds to the action&nbsp; <code>android.intent.action.MAIN</code> . </p> \n <pre class=\"prettyprint\">&lt;activity android:name=&quot;com.hathy.fblogin.MainActivity&quot;&gt;\r\n    &lt;intent-filter&gt;\r\n        &lt;action android:name=&quot;android.intent.action.MAIN&quot;/&gt;\r\n        &lt;category android:name=&quot;android.intent.category.LAUNCHER&quot;/&gt;\r\n    &lt;/intent-filter&gt;\r\n&lt;/activity&gt;</pre> \n <p> Add the application ID as <code>meta-data</code> . </p> \n <pre class=\"prettyprint\">&lt;meta-data android:name=&quot;com.facebook.sdk.ApplicationId&quot; \r\n   android:value=&quot;@string/facebook_app_id&quot;/&gt;</pre> \n <p> Define <code>FacebookActivity</code> as another <code>Activity</code> that belongs to your app. It handles most of the configuration changes itself. You need to mention that using the&nbsp; <code>configChanges</code> attribute. </p> \n <pre class=\"prettyprint\">&lt;activity android:name=&quot;com.facebook.FacebookActivity&quot;\r\n  android:configChanges=\r\n         &quot;keyboard|keyboardHidden|screenLayout|screenSize|orientation&quot;\r\n  android:theme=&quot;@android:style/Theme.Translucent.NoTitleBar&quot;\r\n  android:label=&quot;@string/app_name&quot; /&gt;</pre> \n <p> Finally, you have to request <code>android.permission.INTERNET</code> to be able to&nbsp;connect to Facebook\'s servers. </p> \n <pre class=\"prettyprint\">&lt;uses-permission android:name=&quot;android.permission.INTERNET&quot;/&gt;</pre> \n <h2> <span>6.</span> Build and Run </h2> \n <p>Your app is now complete. When you build it and deploy it on your Android device, you will see the Facebook&nbsp;login button.</p> \n <figure> \n  <img src=\"http://img1.tuicool.com/INnq2uQ.png\" class=\"alignCenter\" /> \n </figure> \n <p>Tapping the login button&nbsp;takes you to a Facebook&nbsp;page that asks you to log in and authorize the app. </p> \n <figure> \n  <img src=\"http://img0.tuicool.com/BV7nUfu.png\" class=\"alignCenter\" /> \n </figure> \n <p> After successfully logging in, the <code>TextView</code> will display the user ID and auth token. </p> \n <figure> \n  <img src=\"http://img2.tuicool.com/y6v2Yb.png\" class=\"alignCenter\" /> \n </figure> \n <h2>Conclusion </h2> \n <p> In this quick tip, you learned how to use the&nbsp;Facebook SDK to add Facebook Login to your Android app. You also learned how to handle the possible&nbsp;outcomes of a login attempt. To learn more about Facebook Login, you can go through the reference for the <a href=\"https://developers.facebook.com/docs/reference/android/current/\" target=\"_self\" rel=\"nofollow,noindex\">Facebook SDK for Android</a> . </p> \n</div>\n        </div>\n        <div class=\"article_social\">\n        <div class=\"article_like\">\n                <button class=\"btn btn-large btn-like\" id=\"my_like2\" data_id=\"fiAJ7zI\">\n                    <i class=\"icon-heart-empty\"></i>\n                    赞一个\n                </button>\n                <button class=\"btn btn-large btn-fav liked\" id=\"my_like\" data_id=\"fiAJ7zI\">\n                    <i class=\"icon-star-empty\"></i>\n                    收 藏\n                </button>\n        </div>\n        </div>\n           <div id=\"site_articles\">\n              <div class=\"article-part-title\">\n                <span>推荐文章</span>\n              </div>\n          <ul class=\"side_article_list\">\n                <li class=\"side_article_list_item\">\n                    1.<a href=\"/articles/YzY77j\" target=\"_blank\" title=\"A simple Cordova task runner for Visual Studio Code\">\n                    A simple Cordova task runner for Visual Studio Code\n                </a>\n                </li>\n                <li class=\"side_article_list_item\">\n                    2.<a href=\"/articles/EZR7Rb\" target=\"_blank\" title=\"Retrospective on Appcelerator Titanium and Alloy\">\n                    Retrospective on Appcelerator Titanium and Alloy\n                </a>\n                </li>\n                <li class=\"side_article_list_item\">\n                    3.<a href=\"/articles/fUb2Yr\" target=\"_blank\" title=\"Xposed Super Alpha for 5.1.x Updated with More Support\">\n                    Xposed Super Alpha for 5.1.x Updated with More Support\n                </a>\n                </li>\n                <li class=\"side_article_list_item\">\n                    4.<a href=\"/articles/JNbq63u\" target=\"_blank\" title=\"The Android Clock App Clinic\">\n                    The Android Clock App Clinic\n                </a>\n                </li>\n                <li class=\"side_article_list_item\">\n                    5.<a href=\"/articles/F7773iZ\" target=\"_blank\" title=\"An Introduction to Volley\">\n                    An Introduction to Volley\n                </a>\n                </li>\n                <li class=\"side_article_list_item\">\n                    6.<a href=\"/articles/zMnu6bj\" target=\"_blank\" title=\"Understanding Android WebViews\">\n                    Understanding Android WebViews\n                </a>\n                </li>\n         </ul>\n        </div>\n\n        <div id=\"kan_articles\"></div>\n        <div id=\"article_weibo\" style=\"display:none;\">\n            <div class=\'article-part-title\'>\n                <span>相关微博</span>\n                <sub>\n                    <a href=\"/articles/weibo_list/fiAJ7zI\" target=\"_blank\">(<i id=\"weibo_num\"></i>)</a> \n               </sub>\n            </div>\n            <div class=\"related-weibo-list\"></div>\n        </div>\n        <div class=\"comments\">\n    <div class=\"comments-area\">\n    <div class=\"comments-header\">\n        <h5>我来评几句</h5>\n        <div class=\"alert comment-alert alert-error\" style=\"display:none;\">\n            错误\n        </div>\n            <textarea cols=\"60\" rows=\"5\" id=\"comment-body\" placeholder=\"请输入评论内容...\"></textarea>\n            <span class=\"btn btn-medium btn-submit\"  id=\"comment-submit\">登录后评论</span>\n        <p style=\"margin-top: 5px;margin-left:10px;\">\n            已发表评论数(<span class=\"comment_cnt\"></span>)\n        </p>\n    </div>\n    <div class=\"comments-list\">\n        <div class=\"empty-cmts alert alert-success\" style=\"display:none;\">\n            没有更多评论了^^\n        </div>\n    </div>\n    <div class=\"more-comments\" style=\"display:none;\">\n        <a href=\"\">更多评论</a>\n    </div>\n    <div class=\"load-fail\" style=\"display:none;\">\n        评论加载失败，<a href=\"javascript:reload_comments(\'fiAJ7zI\',1,0,-1);\">重新加载</a>\n    </div>\n    </div>\n</div>\n\n    </div>\n         <div class=\"span4 article_right_side\">\n            \n            <div class=\"right_top\">\n    <div class=\"article_related_site article_detail_bg\">\n    <h4 class=\"article-part-title\">相关站点</h4>\n    <div class=\"article_related_site_body clearfix\">\n        <div class=\"logo\">\n            <img src=\"http://stimg1.tuicool.com/eQv6Fv.png\"/>\n        </div>\n        <div class=\"name\">\n            <div>\n                    <a href=\"/sites/eQv6Fv\" target=\"_blank\"> Mobiletuts+</a>\n            </div>\n            <div>\n                    <input type=\"button\" class=\"btn btn-success right_site_follow\" value=\"+&nbsp;订阅\" id=\"my_follow\" data_id=\"eQv6Fv\"/>\n            </div>\n        </div>\n    </div>\n</div>\n\n<div id =\"related_topics\" class=\"related_topics2 article_detail_bg\">\n    <h4 class=\"article-part-title\">相关主题</h4>\n    <ul class=\"clearfix\">\n        <li>\n            <a href=\"/topics/11080010\" target=\'_blank\' class=\'kan-item-head\'>\n              <small>安卓开发</small>\n              <img class=\'kan-cover\' src=\"http://ttimg2.tuicool.com/11080010.png\" alt=\"安卓开发\" />\n            </a>\n        </li>\n    </ul>\n</div>\n\n\n\n<div class=\"right-ad\">\n      <script type=\"text/javascript\">BAIDU_CLB_fillSlot(\"1086186\");</script>\n\n</div>\n<div class=\"right-ad\" style=\"margin-top: 8px\">\n      <script type=\"text/javascript\">BAIDU_CLB_fillSlot(\"1086175\");</script>\n\n</div>\n<div class=\"right-ad\" style=\"margin-top: 8px\">\n      <script type=\"text/javascript\">BAIDU_CLB_fillSlot(\"1086183\");</script>\n\n</div>\n<div id=\"right_site_articles\" class=\"article_detail_bg\">\n    <div class=\"article-part-title\">\n        <span>热门文章</span>\n    </div>\n    <ul class=\"side_article_list\">\n        <li class=\"side_article_list_item\">\n            1.<a href=\"/articles/v6NfUzi\" target=\"_blank\" title=\"PhoneGap 3.x Mobile Application Development Hotshot\"> PhoneGap 3.x Mobile Application Development Hotshot </a>\n        </li>\n        <li class=\"side_article_list_item\">\n            2.<a href=\"/articles/FNNreqA\" target=\"_blank\" title=\"Build iOS/Android Libraries in Common Lisp with LispWorks\"> Build iOS/Android Libraries in Common Lisp with LispWorks </a>\n        </li>\n        <li class=\"side_article_list_item\">\n            3.<a href=\"/articles/YzY77j\" target=\"_blank\" title=\"A simple Cordova task runner for Visual Studio Code\"> A simple Cordova task runner for Visual Studio Code </a>\n        </li>\n        <li class=\"side_article_list_item\">\n            4.<a href=\"/articles/3AbmEnY\" target=\"_blank\" title=\"YouTube v10.19.53 APK Download\"> YouTube v10.19.53 APK Download </a>\n        </li>\n        <li class=\"side_article_list_item\">\n            5.<a href=\"/articles/2i6JRnm\" target=\"_blank\" title=\"GravityBox for Xposed Updated to Support Android 5.1\"> GravityBox for Xposed Updated to Support Android 5.1 </a>\n        </li>\n        <li class=\"side_article_list_item\">\n            6.<a href=\"/articles/fUb2Yr\" target=\"_blank\" title=\"Xposed Super Alpha for 5.1.x Updated with More Support\"> Xposed Super Alpha for 5.1.x Updated with More Support </a>\n        </li>\n    </ul>\n</div>\n</div>\n\n<div class=\"operate_zone\">\n    <div class=\"container-body share-body\">\n        <div class=\"article-part-title\">\n            <span>分享本文</span>\n        </div>\n        <div class=\"share_zone\">\n    <div class=\"bdsharebuttonbox\"> \n         <a href=\"#\" class=\"bds_tsina\" data-cmd=\"tsina\" title=\"分享到新浪微博\"></a>\n        <a href=\"#\" class=\"bds_qzone\" data-cmd=\"qzone\" title=\"分享到QQ空间\"></a>\n        <a href=\"#\" class=\"bds_tqq\" data-cmd=\"tqq\" title=\"分享到腾讯微博\"></a>\n        <a href=\"#\" class=\"bds_renren\" data-cmd=\"renren\" title=\"分享到人人网\"></a>\n        <a href=\"#\" class=\"bds_weixin\" data-cmd=\"weixin\" title=\"分享到微信\"></a>\n        <a href=\"#\" class=\"bds_more\" data-cmd=\"more\"></a>\n     </div>\n    <script>\n        window._bd_share_config = {\n            \"common\" : {\n                \"bdSnsKey\" : {\n                    \"tsina\" : \"3113829255\",\n             