insert into `tcnote` (`note_name`, `author_name`, `from_url`, `blob_content`, `create_date`, `flag`, `noteBook`, `noteBookGroup`) values('Apple Amping Up Maps to Compete With Google.html','高广金','http://www.tuicool.com/articles/YBjyYfu','<!DOCTYPE HTML>\n\n<!--\n ______________ \n< TUICOOL.COM >\n -------------- \n        \\   ^__^\n         \\  (**)\\__$__$__\n            (__)\\       )\\/\\\n             U  ||------|\n                ||     ||\n-->\n<head>\n  <meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n  <meta content=\"authenticity_token\" name=\"csrf-param\" />\n<meta content=\"lwV0/vANBrWALkomzGYKBphTZwZN5Eq+7HJosUGYM4I=\" name=\"csrf-token\" />\n    <title>\n            Apple Amping Up Maps to Compete With Google - 推酷\n   </title>\n    <meta name=\"description\" content=\"Apple Amping Up Maps to Compete With Google\"/>\n    <meta property=\"qc:admins\" content=\"2040660764645137746375\" />\n  <link rel=\"shortcut icon\" href=\"http://static0.tuicool.com/favicon.ico\" type=\"image/x-icon\" />\n  <link href=\"http://asset.tuicool.com/assets/application-77e2835bfa2a5c61a6c79d3fcb00cd7b.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" />\n  <link href=\"http://netdna.bootstrapcdn.com/font-awesome/4.0.3/css/font-awesome.min.css\" rel=\"stylesheet\">\n  <script src=\"http://asset.tuicool.com/assets/application-5272668a8ed427c5225cac7c6110da64.js\" type=\"text/javascript\"></script>\n\n  <!--[if IE 7]>\n  <link rel=\"stylesheet\" href=\"http://assets.tuicool.com/assets/font-awesome-ie7.min.css\">\n  <![endif]--> \n    <script type=\"text/javascript\" src=\"http://static2.tuicool.com/assets/tip.js?t=3\"></script>\n  \n  <script type=\"text/javascript\" src=\"http://cbjs.baidu.com/js/m.js\"></script>\n<script type=\"text/javascript\" src=\"http://static1.tuicool.com/assets/spin.min.js\"></script>\n<link rel=\"stylesheet\" href=\"http://static0.tuicool.com/assets/github.css\">\n\n</head>\n<body>\n  \n  <div id=\"header\" class=\"navbar-fixed-top\">\n    <div class=\"container\">\n      <div class=\"navbar\">\n        <div class=\"navbar-inner\">\n          <a class=\"btn btn-navbar\" data-toggle=\"collapse\" data-target=\".nav-collapse\"> \n            <span class=\"icon-bar\"></span> \n            <span class=\"icon-bar\"></span>\n            <span class=\"icon-bar\"></span>\n            <span class=\"icon-bar\"></span> \n          </a>\n          <a href=\"http://www.tuicool.com/\" class=\"brand\">推酷</a>        \n        <nav class=\"nav-collapse collapse\">\n            <ul class=\"nav navbar primary-nav\">                            \n              <li class=\"active\">\n                <a href=\"http://www.tuicool.com/ah\">\n                  文章\n                </a>\n              </li>              \n              <li class=\"\">\n                <a href=\"http://www.tuicool.com/sites/hot\">\n                  站点\n                </a>\n              </li>\n              <li class=\"\">\n                <a href=\"http://www.tuicool.com/topics\">\n                  主题\n                </a>\n              </li>\n              <li class=\"\">\n                <a href=\"http://course.tuicool.com/\">\n                  公开课\n                </a>\n              </li>\n              <li class=\"\">\n                <a href=\"http://huodong.tuicool.com/\">\n                  活动\n                </a>\n              </li>\n              <li class=\"\">\n                <a href=\"http://www.tuicool.com/mobile\">\n                  客户端\n                    <sup style=\"font-size:0.8em;color: #16A085;\">荐</sup>\n                </a>\n              </li>\n              <li class=\"dropdown  \">\n                <a href=\"#\" class=\"dropdown-toggle\" data-toggle=\"dropdown\">周刊 <b class=\"caret\"></b></a>\n                <ul class=\"dropdown-menu\">\n                  <li><a href=\"http://www.tuicool.com/mags\">编程狂人</a></li>\n                  <li><a href=\"http://www.tuicool.com/mags/design\">设计匠艺</a></li> \n                  <li><a href=\"http://www.tuicool.com/articles/weekly\">一周拾遗</a></li>                  \n                </ul>\n              </li>\n              \n               <li class=\"dropdown  \">\n                <a href=\"#\" class=\"dropdown-toggle\" data-toggle=\"dropdown\">更多 <b class=\"caret\"></b></a>\n                <ul class=\"dropdown-menu\">\n                  <li><a href=\"http://www.tuicool.com/bbs\">讨论区</a></li>  \n                  <li><a href=\"http://www.tuicool.com/about\">关于我们</a></li>                 \n                </ul>\n              </li>\n              \n              </ul>\n            <form class=\"navbar-search pull-left\" action=\"http://www.tuicool.com/search\">\n              <input type=\"text\" class=\"search-query span2\" name=\"kw\" placeholder=\"搜索\">\n            </form>\n            <ul class=\"nav pull-right\">\n                <li><a href=\"http://www.tuicool.com/login\">登录</a></li>\n            </ul>\n          </nav>\n        </div>\n      </div>\n  </div>   \n</div>\n  <div id =\"flash_container\" class=\"noPrint\">    \n  </div>\n  \n  <div class=\"container-fluid\">  \n    <div class=\"frd_pos\">\n    <div class=\"row-fluid\" style=\"margin-top: 15px\">\n        <div>\n            <script type=\"text/javascript\">BAIDU_CLB_fillSlot(\"1076478\");</script>\n        </div>\n    </div>\n</div>\n<div class=\"row-fluid article_row_fluid\">\n    <div class=\"span8 contant article_detail_bg\">\n        <h1>Apple Amping Up Maps to Compete With Google</h1>\n        <div class=\"article_meta\">\n            <span class=\"timestamp\">时间&nbsp;2015-05-18 22:52:12\n            </span>\n            <span class=\"from\">\n                <i class=\"icon-globe\"></i>\n                    <a class=\"cut cut28 from\" href=\"/sites/fe2Mfq\" target=\"_blank\">Search Engine Watch\n                    </a>\n            </span>\n            <span class=\"source\" style=\"display:block;\"><i style=\"float:left;\">原文</i>&nbsp; \n                <a class=\"cut cut70\" href=\"http://www.tuicool.com/articles/goto?id=YBjyYfu\" style=\"display:inline-block;\">http://searchenginewatch.com/sew/news/2409039/apple-amping-up-maps-to-compete-with-google</a>\n            </span>\n        </div>\n        <div class=\"article_body\" id=\"nei\">\n            <div itemprop=\"articleBody\">\n  <p>Apple has acquired GPS software company Coherent Navigation in a move to bolster Apple Maps\' location and mapping capabilities.</p>\n  <p>\nApple has not had asmooth ride in the mapping business. Apple Maps was blighted with problems and inaccuracies at launch, and the firm apologised to its customers andsuggested that they use alternatives like Google Maps.  </p>\n  <p>The software has improved since then, and Apple has now boosted its capabilities with the Coherent Navigation buy.</p>\n  <p>Apple rarely comments on acquisitions and has simply released its usual confirmation statement: &quot;Apple buys smaller technology companies from time to time, and we generally do not discuss our purpose or plans.&quot;</p>\n  <p>\nThe    <a href=\"http://www.macrumors.com/2015/05/17/apple-coherent-navigation-acquisition/\" target=\"_blank\" title=\"Macrumours\" rel=\"nofollow,noindex\">\n      <em>MacRumours</em>\nwebsite was first with the news    </a>\n, reporting that senior Coherent Navigation staffers left the company for Apple in the past few weeks and that the company website has been closed down. The domain name now points at Apple\'s servers.  </p>\n  <p>\n    <a href=\"https://www.linkedin.com/in/paulglego\" target=\"_blank\" title=\"Paul Lego\" rel=\"nofollow,noindex\">The Linkedin page of former CEO of Coherent Navigation, Paul Lego</a>\n, lists working at Apple since January 2015, where he simply states he is a &quot;Member of Apple Maps Team&quot;.  </p>\n  <p>Coherent Navigation uses the Iridium satellite network to provide accurate location information, and Lego\'s LinkedIn profile about the company says it works with firms in industries including agriculture, construction, mining, and oil and gas exploration.</p>\n  <p>&quot;Coherent also does both classified and unclassified work for the US government,&quot; he added.</p>\n  <p>Apple has repeatedly added resources and skills to its Mapping business, with the acquisition of a firm called Spotsetter in 2014 which provides social information overlaid on maps, recommending activities, places to eat and sites of interest.</p>\n  <p>\nLast month, Google Mapsalso updated to recommend related search results as part of its mapping features.&nbsp;  </p>\n</div>\n\n        </div>\n        <div class=\"article_social\">\n        <div class=\"article_like\">\n                <button class=\"btn btn-large btn-like\" id=\"my_like2\" data_id=\"YBjyYfu\">\n                    <i class=\"icon-heart-empty\"></i>\n                    赞一个\n                </button>\n                <button class=\"btn btn-large btn-fav liked\" id=\"my_like\" data_id=\"YBjyYfu\">\n                    <i class=\"icon-star-empty\"></i>\n                    收 藏\n                </button>\n        </div>\n        </div>\n           <div id=\"site_articles\">\n              <div class=\"article-part-title\">\n                <span>推荐文章</span>\n              </div>\n          <ul class=\"side_article_list\">\n                <li class=\"side_article_list_item\">\n                    1.<a href=\"/articles/IN3ieaa\" target=\"_blank\" title=\"‘Not for attribution’ quotes are tech reporting’s dirty little secret\">\n                    ‘Not for attribution’ quotes are tech reporting’s dirty litt..\n                </a>\n                </li>\n                <li class=\"side_article_list_item\">\n                    2.<a href=\"/articles/u6FJvam\" target=\"_blank\" title=\"Pinterest Adds Audience Targeting, CPA Model To Promoted Pins\">\n                    Pinterest Adds Audience Targeting, CPA Model To Promoted Pins\n                </a>\n                </li>\n                <li class=\"side_article_list_item\">\n                    3.<a href=\"/articles/eEVzemm\" target=\"_blank\" title=\"New Browser Extension Decides How Trustworthy You Are\">\n                    New Browser Extension Decides How Trustworthy You Are\n                </a>\n                </li>\n                <li class=\"side_article_list_item\">\n                    4.<a href=\"/articles/uymAbeV\" target=\"_blank\" title=\"You can now watch the &#x27;Netflix for pirated movies&#x27; right from your web browser\">\n                    You can now watch the &#x27;Netflix for pirated movies&#x27; right from y..\n                </a>\n                </li>\n                <li class=\"side_article_list_item\">\n                    5.<a href=\"/articles/UVFVVj2\" target=\"_blank\" title=\"iSpot.tv Raises $21.9M To Track The Online Impact Of TV Advertising\">\n                    iSpot.tv Raises $21.9M To Track The Online Impact Of TV Adverti..\n                </a>\n                </li>\n                <li class=\"side_article_list_item\">\n                    6.<a href=\"/articles/Y3mYniV\" target=\"_blank\" title=\"This was Sony Music&#x27;s contract with Spotify\">\n                    This was Sony Music&#x27;s contract with Spotify\n                </a>\n                </li>\n         </ul>\n        </div>\n\n        <div id=\"kan_articles\"></div>\n        <div id=\"article_weibo\" style=\"display:none;\">\n            <div class=\'article-part-title\'>\n                <span>相关微博</span>\n                <sub>\n                    <a href=\"/articles/weibo_list/YBjyYfu\" target=\"_blank\">(<i id=\"weibo_num\"></i>)</a> \n               </sub>\n            </div>\n            <div class=\"related-weibo-list\"></div>\n        </div>\n        <div class=\"comments\">\n    <div class=\"comments-area\">\n    <div class=\"comments-header\">\n        <h5>我来评几句</h5>\n    